sprites.onOverlap(SpriteKind.Cursor1, SpriteKind.play, function (sprite, otherSprite) {
    if (otherSprite == Play && controller.B.isPressed() && difficulty == 1) {
        level = 1
        LevelControll()
    }
    if (otherSprite == Play && controller.B.isPressed() && difficulty == 2) {
        level = 4
        LevelControll()
    }
    if (otherSprite == Play && controller.B.isPressed() && difficulty == 0) {
        level = 6
        LevelControll()
    }
    if (otherSprite == Play && controller.B.isPressed() && difficulty == 3) {
        level = 5
        LevelControll()
    }
})
